export const CATEGORY_NAMES: Record<BrokerCategory, string> = {
  'people-search': 'People Search Sites',
  'location': 'Location Data (ICE-relevant)',
  'advertising': 'Advertising Networks',
  'aggregator': 'Data Aggregators',
  'background-check': 'Background Check Services',
  'financial': 'Financial Data',
  'other': 'Other Data Brokers',
};

export function searchBrokers(query: string): DataBroker[] {
  const q = query.toLowerCase();
  return BROKERS.filter(b =>
    b.name.toLowerCase().includes(q) ||
    b.website.toLowerCase().includes(q) ||
    b.category.toLowerCase().includes(q)
  );
}

const SAFE_PROTOCOLS = new Set(['http:', 'https:']);

export function isSafeHttpUrl(value: string | undefined): boolean {
  if (!value) return false;
  try {
    const parsed = new URL(value.trim());
    return SAFE_PROTOCOLS.has(parsed.protocol);
  } catch {
    return false;
  }
}

export function getSafeUrl(value: string | undefined): string | null {
  if (!value) return null;
  return isSafeHttpUrl(value) ? value.trim() : null;
}

function validateBrokerUrls(): void {
  const invalid = [];
  for (const broker of BROKERS) {
    if (!isSafeHttpUrl(broker.website)) {
      invalid.push(`${broker.name} (website)`);
    }
    if (broker.optOutUrl && !isSafeHttpUrl(broker.optOutUrl)) {
      invalid.push(`${broker.name} (optOutUrl)`);
    }
  }

  if (invalid.length > 0) {
    console.warn(`[MN Privacy Shield] Invalid broker URLs detected: ${invalid.length}`);
    console.warn(invalid.slice(0, 10).join(', '));
  }
}

if (process.env.NODE_ENV !== 'production') {
  validateBrokerUrls();
}
